package com.cody.handler.business.viewmodel;import com.cody.handler.framework.viewmodel.XItemViewModel;/** * Created by chen.huarong on 2018/8/29. */public class ItemTagViewModel extends XItemViewModel {    private int code;    private String value;    public ItemTagViewModel(String value) {        this.value = value;    }    public ItemTagViewModel(int code, String value) {        this.code = code;        this.value = value;    }    public int getCode() {        return code;    }    public void setCode(int code) {        this.code = code;    }    public String getValue() {        return value;    }    public void setValue(String value) {        this.value = value;    }}